/**
 * This file provides the styles for the fullscreen media manager
 * (?do=media).
 *
 * What most templates would probably need to change (depending on
 * their site width) are the 4 min-width's (search for @change).
 */


/*____________ structure ____________*/

#mediamanager__page h1 {
    margin-bottom: .5em;
}

#mediamanager__page {
    /* min-width must be summary of all 3 panels' min-widths */
    min-width: 50em; /* @change */
    width: 100%;
    text-align: left;
}

#mediamanager__page .panel {
    float: left;
}

#mediamanager__page .namespaces {
    width: 20%;
    min-width: 10em; /* @change */
}
#mediamanager__page .filelist {
    width: 50%;
    min-width: 25em; /* @change */
}
#mediamanager__page .file {
    width: 30%;
    min-width: 15em; /* @change */
}

#mediamanager__page .panelHeader {
    background-color: #eee;
    margin: 0 10px 10px 0;
    padding: 10px 10px 8px;
    text-align: left;
    min-height: 20px;
    overflow: hidden;
}

#mediamanager__page .panelContent {
    overflow-y: auto;
    overflow-x: hidden;
    padding: 0;
    margin: 0 10px 10px 0;
    position: relative;
}
[dir=rtl] #mediamanager__page .panelContent {
    text-align: right;
}

#mediamanager__page .file .panelHeader,
#mediamanager__page .file .panelContent {
    margin-right: 0;
}

#mediamanager__page .ui-resizable-e {
    width: 6px;
    right: 2px;
    background: transparent url(/wiki/pm/lib/tpl/dokuwiki_modif/../../images/resizecol.png) center center no-repeat;
}
#mediamanager__page .ui-resizable-e:hover {
    background-color: #eee;
}


#mediamanager__page dd {
    margin: 0;
}

#mediamanager__page .panelHeader h3 {
    float: left;
    font-weight: normal;
    font-size: 1em;
    padding: 0;
    margin: 0 0 3px;
}


/*____________ namespaces panel ____________*/

[dir=rtl] #mediamanager__page .namespaces {
    text-align: right;
}

/* make it look like a tab (as in _tabs.css) */
#mediamanager__page .namespaces h2 {
    font-size: 1em;
    display: inline-block;
    padding: .3em .8em;
    margin: 0 0 0 .3em;
    border-radius: .5em .5em 0 0;
    font-weight: normal;
    background-color: #eee;
    color: #333;
    border: 1px solid #ccc;
    border-bottom-color: #eee;
    line-height: 1.4em;
    position: relative;
    bottom: -1px;
    z-index: 2;
}
* html #mediamanager__page .namespaces h2,
*+html #mediamanager__page .namespaces h2 {
    display: inline;
}
[dir=rtl] #mediamanager__page .namespaces h2 {
    margin: 0 .3em 0 0;
    position: relative;
    right: 10px;
}
#mediamanager__page .namespaces .panelHeader {
    border-top: 1px solid #ccc;
    z-index: 1;
}

#mediamanager__page .namespaces ul {
    margin-left: .2em;
    margin-bottom: 0;
    padding: 0;
    list-style: none;
}
[dir=rtl] #mediamanager__page .namespaces ul {
    margin-left: 0;
    margin-right: .2em;
}
#mediamanager__page .namespaces ul ul {
    margin-left: 1em;
}
[dir=rtl] #mediamanager__page .namespaces ul ul {
    margin-left: 0;
    margin-right: 1em;
}
#mediamanager__page .namespaces ul ul li {
    margin: 0;
}

#mediamanager__page .namespaces ul .selected {
    background-color: #ff9;
    font-weight: bold;
}


/*____________ file list panel ____________*/

/* file list header */

#mediamanager__page .panelHeader form.options {
    float: right;
    margin-top: -3px;
}

#mediamanager__page .panelHeader ul {
    list-style: none;
    margin: 0;
    padding: 0;
}
#mediamanager__page .panelHeader ul li {
    color: #333;
    float: left;
    line-height: 1;
    padding-left: 3px;
}
[dir=rtl] #mediamanager__page .panelHeader ul li {
    margin-right: 0;
    margin-left: .5em;
}

#mediamanager__page .panelHeader ul li.listType {
    padding-left: 30px;
    margin: 0 0 0 5px;
    background: url('/wiki/pm/lib/tpl/dokuwiki_modif/../../images/icon-list.png') 3px 1px no-repeat;
}
#mediamanager__page .panelHeader ul li.sortBy {
    padding-left: 30px;
    margin: 0 0 0 5px;
    background: url('/wiki/pm/lib/tpl/dokuwiki_modif/../../images/icon-sort.png') 3px 1px no-repeat;
}

#mediamanager__page .panelHeader form.options .ui-buttonset label{
    font-size: 90%;
    margin-right: -0.4em;
}
#mediamanager__page .panelHeader form.options .ui-buttonset .ui-button-text {
    padding: .3em .5em;
    line-height: 1;
}

/* file list content */

#mediamanager__page .filelist ul {
    padding: 0;
    margin: 0 10px 0 0;
}
[dir=rtl] #mediamanager__page .filelist ul {
    margin: 0 10px 0 0;
}

#mediamanager__page .filelist .panelContent ul li:hover {
    background-color: #eee;
}

#mediamanager__page .filelist li dt a {
    vertical-align: middle;
    display: table-cell;
    overflow: hidden;
}
* html #mediamanager__page .filelist .thumbs li dt a,
*+html #mediamanager__page .filelist .thumbs li dt a {
    display: block;
}
* html #mediamanager__page .filelist .rows li dt a,
*+html #mediamanager__page .filelist .rows li dt a {
    display: inline;
}

/* file list as thumbs */

#mediamanager__page .filelist .thumbs li {
    width: 100px;
    min-height: 130px;
    display: inline-block;
    display: -moz-inline-stack;
    /* the right margin should visually be 10px, but because of its inline-block nature the whitespace inbetween is about 4px more */
    margin: 0 6px 10px 0;
    background-color: #ddd;
    color: #333;
    padding: 5px;
    vertical-align: top;
    text-align: center;
    position: relative;
    line-height: 1.2;
}
[dir=rtl] #mediamanager__page .filelist .thumbs li {
    margin-right: 0;
    margin-left: 6px;
}
* html #mediamanager__page .filelist .thumbs li,
*+html #mediamanager__page .filelist .thumbs li {
    display: inline;
    zoom: 1;
}

#mediamanager__page .filelist .thumbs li dt a {
    width: 100px;
    height: 90px;
}

#mediamanager__page .filelist .thumbs li dt a img {
    max-width: 90px;
    max-height: 90px;
}

#mediamanager__page .filelist .thumbs li .name,
#mediamanager__page .filelist .thumbs li .size,
#mediamanager__page .filelist .thumbs li .filesize,
#mediamanager__page .filelist .thumbs li .date {
    display: block;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 90px;
    white-space: nowrap;
}
#mediamanager__page .filelist .thumbs li .name {
    padding: 5px 0;
    font-weight: bold;
}
#mediamanager__page .filelist .thumbs li .date {
    font-style: italic;
    white-space: normal;
}

/* file list as rows */

#mediamanager__page .filelist .rows li {
    list-style: none;
    display: block;
    position: relative;
    max-height: 50px;
    margin: 0;
    margin-bottom: 3px;
    background-color: #fff;
    color: #333;
    overflow: hidden;
}

#mediamanager__page .filelist .rows li:nth-child(2n+1) {
    background-color: #ddd;
}

#mediamanager__page .filelist .rows li dt {
    float: left;
    width: 10%;
    height: 40px;
    text-align: center;
}

#mediamanager__page .filelist .rows li dt a {
    width: 100px;
    height: 40px;
}

#mediamanager__page .filelist .rows li dt a img {
    max-width: 40px;
    max-height: 40px;
}

#mediamanager__page .filelist .rows li .name,
#mediamanager__page .filelist .rows li .size,
#mediamanager__page .filelist .rows li .filesize,
#mediamanager__page .filelist .rows li .date {
    overflow: hidden;
    text-overflow: ellipsis;
    float: left;
    margin-left: 1%;
    white-space: nowrap;
}

#mediamanager__page .filelist .rows li .name {
    width: 30%;
    font-weight: bold;
}
#mediamanager__page .filelist .rows li .size,
#mediamanager__page .filelist .rows li .filesize {
    width: 15%;
}
#mediamanager__page .filelist .rows li .date {
    width: 20%;
    font-style: italic;
    white-space: normal;
}

/* upload form */

#mediamanager__page div.upload {
    padding-bottom: 0.5em;
}

/*____________ file panel ____________*/

