    color: #080;
    background-color: inherit;
}
/* not existing wikipage */
.dokuwiki a.wikilink2 {
    color: #d30;
    background-color: inherit;
}


/*____________ images ____________*/

/* embedded images (styles are already partly set in lib/styles/all.css) */
.dokuwiki img.media {
    margin: .2em 0;
}
.dokuwiki img.medialeft {
    margin: .2em 1em .2em 0;
}
.dokuwiki img.mediaright {
    margin: .2em 0 .2em 1em;
}
.dokuwiki img.mediacenter {
    margin: .2em auto;
}


/*____________ lists ____________*/

#dokuwiki__content ul li,
#dokuwiki__aside ul li {
    color: #999;
}
#dokuwiki__content ol li,
#dokuwiki__aside ol li {
    color: #666;
}
#dokuwiki__content li .li,
#dokuwiki__aside li .li {
    color: #333;
}


/*____________ tables ____________*/

/* div around each table */
.dokuwiki div.table {
    overflow-x: auto;
    margin-bottom: 1.4em;
}
.dokuwiki div.table table {
    margin-bottom: 0;
}

.dokuwiki table.inline {
    min-width: 50%;
}
.dokuwiki table.inline tr:hover td {
    background-color: #eee;
}
.dokuwiki table.inline tr:hover th {
    background-color: #ccc;
}


/*____________ code ____________*/

/* fix if background-color hides underlining */
.dokuwiki em.u code {
    text-decoration: underline;
}

/* for code in <file> */
.dokuwiki pre.file {
}

/* filenames for downloadable file and code blocks */
.dokuwiki dl.code,
.dokuwiki dl.file {
}

.dokuwiki dl.code dt,
.dokuwiki dl.file dt {
    background-color: #fbfaf9;
    /* background: -moz-linear-gradient(   top, #eee 0%, #fbfaf9 100%); see FS#2447 */
    background: -webkit-linear-gradient(top, #eee 0%, #fbfaf9 100%);
    background: -o-linear-gradient(     top, #eee 0%, #fbfaf9 100%);
    background: -ms-linear-gradient(    top, #eee 0%, #fbfaf9 100%);
    background: linear-gradient(        top, #eee 0%, #fbfaf9 100%);
    color: inherit;
    border: 1px solid #ccc;
    border-bottom-color: #fbfaf9;
    border-top-left-radius: .3em;
    border-top-right-radius: .3em;
    padding: .3em .6em .1em;
    margin-bottom: -1px;
    float: left;
}
[dir=rtl] .dokuwiki dl.code dt,
[dir=rtl] .dokuwiki dl.file dt {
    float: right;
}
.dokuwiki dl.code dt a,
.dokuwiki dl.file dt a {
    background-color: transparent;
    font-size: 0.875em;
    font-weight: normal;
    display: block;
    min-height: 16px;
}

.dokuwiki dl.code dd,
.dokuwiki dl.file dd {
    margin: 0;
    clear: left;
    min-height: 1px; /* for IE7 */
}
[dir=rtl] .dokuwiki dl.code dd,
[dir=rtl] .dokuwiki dl.file dd {
    clear: right;
}

.dokuwiki dl.code pre,
.dokuwiki dl.file pre {
    box-shadow: inset -4px -4px .5em -.3em #ccc;
}


/*____________ JS popup ____________*/

.JSpopup {
    background-color: #fff;
    color: #333;
    border: 1px solid #ccc;
    box-shadow: .1em .1em .1em #ccc;
    border-radius: 2px;
    padding: .3em .5em;
    font-size: .9em;
}
.dokuwiki form.search div.ajax_qsearch {
    top: -.35em;
    font-size: 1em;
    text-overflow: ellipsis;
}

.JSpopup ul,
.JSpopup ol {
    padding-left: 0;
}
[dir=rtl] .JSpopup ul,
[dir=rtl] .JSpopup ol {
    padding-right: 0;
}


