body {
    /*
    font: normal 12pt/1.2 serif;
    color: #000;
    background-color: #fff;
    */
}

/* hide certain sections */
.a11y,
#dokuwiki__header .tools,
#dokuwiki__aside,
.dokuwiki .breadcrumbs,
#dw__toc,
h3.toggle,
#dokuwiki__pagetools,
#dokuwiki__footer {
    display: none;
}

.dokuwiki h1,
.dokuwiki h2,
.dokuwiki h3,
.dokuwiki h4,
.dokuwiki h5,
.dokuwiki caption,
.dokuwiki legend {
    clear: both;
}
.dokuwiki ul {
    list-style: disc outside;
}
.dokuwiki ol {
    list-style: decimal outside;
}
.dokuwiki ol ol {
    list-style-type: lower-alpha;
}
.dokuwiki ol ol ol {
    list-style-type: upper-roman;
}
.dokuwiki ol ol ol ol {
    list-style-type: upper-alpha;
}
.dokuwiki ol ol ol ol ol {
    list-style-type: lower-roman;
}

.dokuwiki a:link,
.dokuwiki a:visited {
    text-decoration: underline;
    color: #333;
    background-color: inherit;
}

/* display href after link */
a.urlextern:after,
a.interwiki:after,
a.mail:after {
   content: " [" attr(href) "]";
   font-size: 90%;
}

/* code blocks */
.dokuwiki pre {
    font-family: monospace;
}
.dokuwiki dl.code dt,
.dokuwiki dl.file dt {
    font-weight: bold;
}

/* images */
.dokuwiki img {
    border-width: 0;
    vertical-align: middle;
}
.dokuwiki img.media {
    margin: .2em 0;
}
.dokuwiki img.medialeft {
    margin: .2em 1em .2em 0;
}
.dokuwiki img.mediaright {
    margin: .2em 0 .2em 1em;
}
.dokuwiki img.mediacenter {
    margin: .2em auto;
}

.dokuwiki blockquote {
    padding: 0 10pt;
    margin: 0;
    border: solid #ccc;
    border-width: 0 0 0 2pt;
}
[dir=rtl] .dokuwiki blockquote {
    border-width: 0 2pt 0 0;
}

/* tables */
.dokuwiki table {
    border-collapse: collapse;
    empty-cells: show;
    border-spacing: 0;
    border: 1pt solid #ccc;
}
.dokuwiki th,
.dokuwiki td {
    padding: 3pt 5pt;
    margin: 0;
    vertical-align: top;
    border: 1pt solid #666;
    text-align: left;
}
[dir=rtl] .dokuwiki th,
[dir=rtl] .dokuwiki td {
    text-align: right;
}
.dokuwiki th {
    font-weight: bold;
}


/*____________ a bit of layout ____________*/

#dokuwiki__header {
    border-bottom: 2pt solid #ccc;
}
#dokuwiki__header h1 {
    font-size: 1.5em;
}
#dokuwiki__header h1 a {
    text-decoration: none;
}
#dokuwiki__header h1 img {
    float: left;
    margin-right: .5em;
}
[dir=rtl] #dokuwiki__header h1 img {
    float: right;
    margin-right: 0;
    margin-left: .5em;
}
.dokuwiki div.footnotes {
    clear: both;
    border-top: 1pt solid #000;
    margin-top: 10pt;
}

} /* /@media END print styles */
